/* vim: set et sw=2 ts=2: */

function makeNodash(options) {
  'use strict';

  options = options || {};

  // Basic ECMA Script 5 checks (if these fail pull in `es5-shim`).
  if (typeof Object.keys !== 'function' || Object.keys({ x: 7 })[0] !== 'x' ||
      typeof Array.isArray !== 'function' || !Array.isArray([]) ||
      typeof Array.prototype.forEach !== 'function') {
    throw new Error('ES5 environment required (`es5-shim` will do).');
  }

  // This is the object the nodash functions will be attached to.
  var Nodash = {};
  
  var register = require('nodash/lib/register')(Nodash, options);

  register('curried', require('nodash/lib/curried'));

  register(require('nodash/lib/type'));
  register(require('nodash/lib/function'));
  
  register(require('nodash/lib/Thunk'));
  register(require('nodash/lib/Tuple'));
  register(require('nodash/lib/List'));
  register(require('nodash/lib/Stream'));

  register(require('nodash/lib/boolean'));
  register(require('nodash/lib/eq'));
  register(require('nodash/lib/ord'));
  register(require('nodash/lib/char'));
  register(require('nodash/lib/num'));
  register(require('nodash/lib/integral'));
  register(require('nodash/lib/fractional'));
  register(require('nodash/lib/floating'));
  register(require('nodash/lib/realfrac'));
  register(require('nodash/lib/numeric'));
  register(require('nodash/lib/string'));
  register(require('nodash/lib/control'));
  register(require('nodash/lib/object'));
  register(require('nodash/lib/match'));

  register(require('nodash/lib/coll/map'));
  register(require('nodash/lib/coll/fold'));
  register(require('nodash/lib/coll/base'));
  register(require('nodash/lib/coll/zipWith'));
  register(require('nodash/lib/coll/extra'));
  register(require('nodash/lib/collection'));
  
  register(require('nodash/lib/Maybe'));
  register(require('nodash/lib/Either'));

  
  register('install', function (mountpoint) {
    var options = arguments[1];
    var nodashObject = Nodash;
    var prefix = '';
    var postfix = '';
    if (!mountpoint) {
      return Nodash;
    }
    if (options) {
      nodashObject = makeNodash(options);
    }
    if (Nodash.isArray(mountpoint)) {
      if (Nodash.isString(mountpoint[0])) {
        prefix = [].shift.call(mountpoint);
      }
      if (Nodash.isString(mountpoint[1])) {
        postfix = mountpoint[1];
      }
      mountpoint = mountpoint[0] || {};
    }
    Nodash.each(function (func, name) {
      if (name[0] === '_') {
        return;
      }
      var key = prefix + name + postfix;
      if (!(key in mountpoint)) {
        mountpoint[key] = func;
      }
    }, nodashObject);
    return mountpoint;
  });

  register('register', register);

  delete Nodash.Thunk;
  delete Nodash.resolveThunk;
  
  register(['freeze', function (freeze) {
    freeze(Nodash); return {};
  }]);
  
  return Nodash;
}

var Nodash = makeNodash();

module.exports = Nodash;

if (typeof window !== 'undefined') {
  window.Nodash = Nodash;
}
